      "Fiber":{"small":"M100,70 l10,-10 h45 v20 h-45 v0 l-10,-10z", "large":"M100,70 l10,-10 a 50 50, 1, 1, 1, 0 60z",
        "name":"Fibers","percentage":"70.9%",
