      }
	  function unRoll(evt){
      var id = evt.target.getAttribute('id').split('-')[0];
	  	document.getElementById(id+"-path").setAttributeNS(null,"class","unrolled");
	  	document.getElementById(id+"-path").setAttribute('d',pointers[id]['large'])
      document.getElementById(id+"-pic").setAttribute('opacity','1');
      document.getElementById(id+"-hidden").setAttribute('d',pointers[id]['large'])
      document.getElementById(id+"-text").firstChild.data = pointers[id]['name'];
      document.getElementById(id+"-pct").firstChild.data = pointers[id]['percentage'];
      document.getElementById(id+"-long-0").firstChild.data = pointers[id]["longtext"][0];
      document.getElementById(id+"-long-1").firstChild.data = pointers[id]["longtext"][1];
      document.getElementById(id+"-long-2").firstChild.data = pointers[id]["longtext"][2];
	  }
	  function rollUp(evt){
      var id = evt.target.getAttribute('id').split('-')[0];
      document.getElementById(id+"-pic").setAttribute('opacity','0');
	  	document.getElementById(id+"-path").setAttributeNS(null,"class","rolled");
	  	document.getElementById(id+"-path").setAttribute('d',pointers[id]['small'])
      document.getElementById(id+"-hidden").setAttribute('d',pointers[id]['small'])
      document.getElementById(id+"-pct").firstChild.data = ' ';
      document.getElementById(id+"-text").firstChild.data = id+'s';
      document.getElementById(id+"-long-0").firstChild.data = " ";
      document.getElementById(id+"-long-1").firstChild.data = " ";
      document.getElementById(id+"-long-2").firstChild.data = " ";
	  }
