svg.script:
    function init(evt){
    if ( window.svgDocument == null ) {
      svgDoc = evt.target.ownerDocument;
    }
      for (pointer in pointers){
        document.getElementById(pointer + '-path').setAttribute('d',pointers[pointer]['small'])
        document.getElementById(pointer + '-hidden').setAttribute('d',pointers[pointer]['small'])
      }
    }
	  var pointers = {
      "Bead":{"small":"M100,2 l10,-25 h45 v20 h-35 v0 l-10,0z", "large":"M100,2 l10,-25 h180 v80 h-180 v-60 l0,0z", 
        "name":"Beads and Pellets","percentage":"1.6%"},
      "Film":{"small":"M100,6.5 l10,-10 h40 v20 h-40 v0 l-10,-10z", "large":"M100,6.5 l10,-10 h180 v80 h-180 v-60 l-10,-10z",
        "name":"Films","percentage":"3.3%"},
      "Foam":{"small":"M100,18 l10,-10 h45 v20 h-45 v0 l-10,-10z", "large":"M100,18 l10,-10 h180 v80 h-180 v-60 l-10,-10z",
        "name":"Foams","percentage":"7.6%"},
      "Fragment":{"small":"M100,33.3 l10,-10 h70 v20 h-70 v0 l-10,-10z", "large":"M100,33.3 l10,-10 h180 v80 h-180 v-60 l-10,-10z",
        "name":"Fragments","percentage":"16.7%"},
      "Fiber":{"small":"M100,70 l10,-10 h43 v20 h-43 v0 l-10,-10z", "large":"M100,70 l10,-10 h180 v80 h-180 v-60 l-10,-10z",
        "name":"Fibers and Lines","percentage":"70.9%"}
      }
	  function unRoll(evt){
      var id = evt.target.getAttribute('id').split('-')[0];
	  	document.getElementById(id+"-path").setAttributeNS(null,"class","Pellet-large");
	  	document.getElementById(id+"-path").setAttribute('d',pointers[id]['large'])
      document.getElementById(id+"-hidden").setAttribute('d',pointers[id]['large'])
      document.getElementById(id+"-text").firstChild.data = pointers[id]['name'];
	  }
	  function rollUp(evt){
      var id = evt.target.getAttribute('id').split('-')[0];
	  	document.getElementById(id+"-path").setAttributeNS(null,"class","Pellet-small");
	  	document.getElementById(id+"-path").setAttribute('d',pointers[id]['small'])
      document.getElementById(id+"-hidden").setAttribute('d',pointers[id]['small'])
      document.getElementById(id+"-text").firstChild.data = id+'s';
	  }
